def sort(xs: List[T]): List[T] =
    if xs.isEmpty:
        return xs
    else:
        pivot = xs.head
        q = List.Nil
        while q.isEmpty or q.head < pivot:
            q = q.cons(xs.head)
        q = q.drop(1)
        xs = q.reverse.append(xs.drop(1)).reverse
        xs.insert(0, pivot)
