public class PowerOfThree {

    public boolean isPowerOfThree(int n) {
        // Base case: if n is 0, then it is a power of 3
        if (n == 0) return true;
        if (n < 0) return false;

        // Recursive case: if n is a power of 3, return true, otherwise recurse and check if n is a power of 3
        return n % 3 == 0 ? true : isPowerOfThree(n / 3);
    }
}

