public class PowerOfThree {

    public boolean isPowerOfThree(int n) {
        // Base case: if n is 0, then it is a power of 3
        if (n == 0) return true;
        while (n > 0) {
            if (n % 3 == 0) return true;
            n = n / 3;
        }
        return false;
    }
}

