class Queue:
    def __init__(self):
        self.head = None
        self.tail = None
    def enqueue(self, data):
        self.head = self.tail
        self.tail = self.tail.next
        self.tail.next = data
    def dequeue(self):
        current = self.head
        if current is None:
            return None
        data = current.data
        current = current.next
        if current is None:
            self.tail = current
        else:
            current.next = self.tail
        self.tail = current
        return data
